import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexDirection: 'column',
        color: 'white',
        fontFamily: 'Nunito',
        [theme.breakpoints.down("md")]: {
            marginTop: 900,
        }

    },
    title: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        fontSize: 50,
    },
    rocketHappy: {
        height: 250,
    }
}));

export default useStyles;
