import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
    },
    emoji: {
        height: 30,
        marginRight: 10,
        marginTop: -5,
    },
}));

export default useStyles;
