import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        fontFamily: 'Nunito',
        fontSize: 14,
        color: 'white',
        //alignItems: 'center',
    },
    imageSize: {
        height: 250,
        [theme.breakpoints.down("md")]: {
            height: 100,
        }
    },
    jobInformation: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        fontFamily: 'Nunito',
        flexDirection: 'column',
    },
    titleEnterprise: {
        fontSize: 28,
    }
}));

export default useStyles;
