import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        fontFamily: 'Nunito',
        fontSize: 14,
        color: 'white',
        //alignItems: 'center',
    },
    imageSize: {
        height: 250,
        [theme.breakpoints.down("md")]: {
            height: 100,
        }
    },
    textStyle: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        fontFamily: 'Nunito',
        fontSize: 18,
    }
}));

export default useStyles;
