import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexDirection: 'column',
        color: 'white',
        fontFamily: 'Nunito',
        [theme.breakpoints.down("md")]: {
            marginTop: 800,
        }

    },
    title: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        fontSize: 50,
    }
}));

export default useStyles;
