import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        color: 'white',
        fontFamily: 'Nunito',

    }
}));

export default useStyles;
