import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        [theme.breakpoints.down("md")]: {
            flexDirection: 'column'
        }

    },
    csharpLogo: {
        width: '7%',
        marginLeft: '64%',
    },
    reactLogo: {
        width: '5%',
        marginLeft: '10%'
    },
    dockerLogo: {
        width: '6%',
        marginLeft: '89%',
    }
}));

export default useStyles;
