import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        fontFamily: 'Nunito',
        fontSize: 14,
        color: 'white',
        margin: 'auto',
        overflow: 'auto',
    },
    contentImage: {
        display: 'flex',
    },
    imageSize: {
        height: 300,
        [theme.breakpoints.down("md")]: {
            height: 150,
        },
    },
    contentJob: {
        display: 'flex',
        [theme.breakpoints.down("md")]: {
            flexDirection: 'row-reverse',
        }
    },
    jobInformation: {
        display: 'flex',
        justifyContent: 'center',
        fontFamily: 'Nunito',
        flexDirection: 'column',
        [theme.breakpoints.down("md")]: {
            marginLeft: 30,
        }

    },titleEnterprise: {
        fontSize: 35,
        [theme.breakpoints.down("md")]: {
            fontSize: 22,
        }
    },
    workTime: {
        marginTop: -17,
        fontSize: 22,
        [theme.breakpoints.down("md")]: {
            fontSize: 12,
        },
    },
    description: {
        [theme.breakpoints.down("md")]: {
            fontSize: 12,
        },
    }
}));

export default useStyles;
