import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
        color: 'white',
        // [theme.breakpoints.down("md")]: {

        // }

    },
    title: {
        fontFamily: 'Nunito',
        fontSize: 50,
    },
    content: {
        backgroundColor: '#3385ff',
        width: '65%',
        height: 500,
        borderRadius: 8,
        display: 'flex',
        flexDirection: 'row',
        padding: 20,
    },
    contentImageProfile: {
        display: 'flex',
    },
    contentDescriptionProfile: {
        display: 'flex',
    },
    imageProfile: {
        width: '200px !important',
        height: '200px !important',
    },
    earth: {
        width: '17%',
        marginLeft: '5%',
    },
    moon: {
        width: '9%',
        marginLeft: '75%',
    },
    jsLogo: {
        marginLeft: '85%',
    }
}));

export default useStyles;
