import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
        color: 'white',

    },
    title: {
        fontFamily: 'Nunito',
        fontSize: 50,
    },
    content: {
        backgroundColor: '#3385ff',
        width: '65%',
        height: 500,
        borderRadius: 8,
        display: 'flex',
        flexDirection: 'row',
        padding: 20,
        [theme.breakpoints.down("md")]: {
            alignItems: 'center',
            flexDirection: 'column',
        }
    },
    contentImageProfile: {
        display: 'flex',
        padding: 10,
    },
    contentDescriptionProfile: {
        display: 'flex',
        padding: 10,
        margin: 20,
        flexDirection: 'column',
    },
    imageProfile: {
        width: '200px !important',
        height: '200px !important',
        [theme.breakpoints.down("md")]: {
            width: '100px !important',
            height: '100px !important',
        }
    },
    earth: {
        width: '17%',
        marginLeft: '5%',
        [theme.breakpoints.down("md")]: {
            width: '24%',
            marginTop: 110,
        }
    },
    moon: {
        width: '9%',
        marginLeft: '75%',
        [theme.breakpoints.down("md")]: {
            width: '15%',
            marginTop: 70,
        }
    },
    jsLogo: {
        marginLeft: '85%',
        [theme.breakpoints.down("md")]: {
            marginLeft: '75%',
        }
    },
    emoji: {
        height: 30,
        marginRight: 10,
        marginTop: 10,
    },
    lineTags: {
        display: 'inline',
    }
}));

export default useStyles;
