import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexDirection: 'column',
        color: 'white',
        fontFamily: 'Nunito',
        [theme.breakpoints.down("md")]: {
            marginTop: 900,
        }

    },
    title: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        fontSize: 50,
    },
    projectBackground: {
        background: 'orange',
        width: 200,
        height: 200,
        borderRadius: '63% 37% 89% 24% / 39% 25% 75% 62% '
    }
}));

export default useStyles;
